import 'dart:async';

import 'package:angular/angular.dart';
import 'package:angular/core.dart';
import 'package:angular_forms/angular_forms.dart';
import 'package:game_client/src/game_model/model.dart';
import 'package:game_client/src/services/app_service.dart';
import 'package:game_client/src/services/game_service.dart';
import 'package:game_client/src/services/gateway_service.dart';
import 'package:core/model/model.dart' as core;

@Component(
    selector: 'action-feedback',
    directives: [coreDirectives, formDirectives],
    template: """
      <div class='action-feedback-cont'>
        <div 
          *ngFor='let action of displayedActions'
          [ngStyle]='{"color": action.color}'
          class='row'
          >
          <div class='col'>
            <img *ngFor='let diceNumber of action.dices' [src]='action.getImagePath(diceNumber)' class='action-feedback__dice-image'>
            <span>{{action.label}}</span>
          </div>
        </div>
      </div>
    """,
    changeDetection: ChangeDetectionStrategy.OnPush)
class ActionsFeedback {
  List<ActionFeedbackData> displayedActions = [];
  GameService gameService;
  AppService appService;
  GatewayService gateway;
  final ChangeDetectorRef changeDetector;

  ActionsFeedback(this.gameService, this.gateway, this.changeDetector, this.appService) {
    this.gameService.unitCreateOrUpdateAction.listen(handleUnitCreateOrUpdate);
  }

  void handleUnitCreateOrUpdate(EnhancedUnitCreateOrUpdateAction cont) {
    var action = cont.action;
    if (action.explain == null) {
      return;
    }
    ClientUnit unit = cont.unit;
    ActionFeedbackData feedback = ActionFeedbackData();
    // TODO: translations
    if (action.explain == core.ActionExplanation.unitAttacking) {
      feedback
        ..dices = action.diceNumbers
        ..label = "unit ${unit.name} attacking"
        ..color = unit.player.color;
    }
    if (action.explain == core.ActionExplanation.unitGotDamage) {
      feedback
        ..label = "unit ${unit.name} got damage ${action.explainFirstValue}"
        ..color = unit.player.color;
    }
    if (action.explain == core.ActionExplanation.unitHealing) {
      feedback
        ..dices = action.diceNumbers
        ..label = "unit ${unit.name} healing unit"
        ..color = unit.player.color;
    }
    if (action.explain == core.ActionExplanation.unitHealed) {
      feedback
        ..label = "unit ${unit.name} has healed with power ${action.explainFirstValue}"
        ..color = unit.player.color;
    }
    displayedActions.add(feedback);
    changeDetector.markForCheck();
    Future.delayed(Duration(seconds: 3)).then((_) {
      displayedActions.remove(feedback);
      changeDetector.markForCheck();
    });
  }

  static List<String> diceImages = [
    "data:image/png;base64,",
    "data:image/png;base64,",
    "data:image/png;base64,",
    "data:image/png;base64,",
    "data:image/png;base64,",
    "data:image/png;base64,"
  ];
}

class ActionFeedbackData {
  List<int> dices = [];
  String label = "";
  String color;

  String getImagePath(int diceNumber) {
    return ActionsFeedback.diceImages[diceNumber];
  }
}
